import React, { useEffect, useRef } from "react";
import "./App.css";
import axios from 'axios';
function App() {
  let videoRef = useRef(null);
  let photoRef = useRef(null)
  const getVideo = () => {
    navigator.mediaDevices
      .getUserMedia({
        video: true
      })
      .then((stream) => {
        let video = videoRef.current;
        video.srcObject = stream;
        video.play();
      })
      .catch((err) => {
        console.error(err);
      });
  };
  const takePicture = () => {
    const width = 400
    const height = width / (16 / 9)
    let video = videoRef.current
    let photo = photoRef.current
    photo.width = width
    photo.height = height
    let ctx = photo.getContext('2d')
    ctx.drawImage(video, 0, 0, width, height)  
  }
  const clearImage = () => {
    let photo = photoRef.current
    let ctx = photo.getContext('2d')
    ctx.clearRect(0,0,photo.width,photo.height)
  }
  const uploadHandler = () => {
    let photo = photoRef.current
    let ctx = photo.getContext('2d')
    axios.post('https://aq9mlslujf.execute-api.us-east-1.amazonaws.com/FaceSimilarityPOC', photo)
      .then((res) => console.log('check response :', setData(res.data[0])))
      .catch((e) => console.log('check error :', e))
  }
  useEffect(() => {
    getVideo();
  }, [videoRef]);
  return (
    <div className="container">
      <h1 className="text-center">Camera Selfie App in React</h1>
      <video ref={videoRef} className="container"></video>
      <button onClick={takePicture} className="btn btn-danger container">Take Picture</button>
      <canvas className="container" ref={photoRef}></canvas>
      <button onClick={uploadHandler} >Upload Image</button>
      <br/><br/>
    </div>
  );
}
export default App;
